import React, { useState } from 'react';
import axios from 'axios';

export default function App() {
  const [url, setUrl] = useState('');
  const [clips, setClips] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Caption customization state
  const [captionStyle, setCaptionStyle] = useState({
    fontFamily: 'Arial',
    fontSize: 24,
    color: '#FFFFFF',
    outline: true,
    position: 'bottom',
  });

  // Background overlay selection
  const [background, setBackground] = useState('none');

  const fetchClips = async e => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setClips([]);

    try {
      let videoUrlParam = url.trim();

      // 1) If not a URL, treat as channel ID
      if (!/^https?:\/\//i.test(videoUrlParam)) {
        const { data: vidData } = await axios.get(
          `${process.env.REACT_APP_API_BASE}/api/videos`,
          { params: { channelId: videoUrlParam } }
        );
        videoUrlParam = `https://youtu.be/${vidData.videoId}`;
      }

      // 2) Fetch up to 3 clips (we could send captionStyle & background here too)
      const { data: clipsData } = await axios.get(
        `${process.env.REACT_APP_API_BASE}/api/clips`,
        { params: { videoUrl: videoUrlParam } }
      );
      setClips(clipsData.clips);
    } catch (err) {
      setError(err.response?.data?.message || err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">AutoClipper-Web</h1>

      <form onSubmit={fetchClips} className="space-y-6">
        {/* URL or Channel ID */}
        <div>
          <label className="block mb-1">
            YouTube Channel ID (UC…) or Full Video URL
          </label>
          <input
            type="text"
            className="w-full border rounded p-2"
            value={url}
            onChange={e => setUrl(e.target.value)}
            placeholder="Enter channel ID or video URL"
            required
          />
        </div>

        {/* Caption Styling Controls */}
        <fieldset className="border rounded p-4">
          <legend className="px-2 font-semibold">Caption Style</legend>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block mb-1">Font Family</label>
              <select
                value={captionStyle.fontFamily}
                onChange={e =>
                  setCaptionStyle({ ...captionStyle, fontFamily: e.target.value })
                }
                className="border rounded p-1 w-full"
              >
                {['Arial','Courier New','Georgia','Impact','Times New Roman'].map(f => (
                  <option key={f} value={f}>{f}</option>
                ))}
              </select>
            </div>
            <div>
              <label className="block mb-1">Font Size</label>
              <input
                type="number"
                value={captionStyle.fontSize}
                onChange={e =>
                  setCaptionStyle({ ...captionStyle, fontSize: Number(e.target.value) })
                }
                className="border rounded p-1 w-full"
                min={12}
              />
            </div>
            <div>
              <label className="block mb-1">Color</label>
              <input
                type="color"
                value={captionStyle.color}
                onChange={e =>
                  setCaptionStyle({ ...captionStyle, color: e.target.value })
                }
                className="border rounded p-1"
              />
            </div>
            <div className="flex items-center">
              <input
                type="checkbox"
                checked={captionStyle.outline}
                onChange={e =>
                  setCaptionStyle({ ...captionStyle, outline: e.target.checked })
                }
                className="mr-2"
              />
              <label>Outline</label>
            </div>
            <div className="col-span-2">
              <label className="block mb-1">Position</label>
              <select
                value={captionStyle.position}
                onChange={e =>
                  setCaptionStyle({ ...captionStyle, position: e.target.value })
                }
                className="border rounded p-1 w-full"
              >
                {['bottom','center','top'].map(pos => (
                  <option key={pos} value={pos}>{pos}</option>
                ))}
              </select>
            </div>
          </div>
        </fieldset>

        {/* Background Overlay Selection */}
        <fieldset className="border rounded p-4">
          <legend className="px-2 font-semibold">Background Overlay</legend>
          <select
            value={background}
            onChange={e => setBackground(e.target.value)}
            className="border rounded p-2 w-full"
          >
            {[
              { value:'none', label:'None' },
              { value:'minecraft', label:'Minecraft Parkour' },
              { value:'gta5', label:'GTA5 Gameplay' },
              { value:'rocketleague', label:'Rocket League' },
            ].map(opt => (
              <option key={opt.value} value={opt.value}>{opt.label}</option>
            ))}
          </select>
        </fieldset>

        <button
          type="submit"
          disabled={loading}
          className="bg-blue-600 text-white px-4 py-2 rounded disabled:opacity-50 w-full"
        >
          {loading ? 'Analyzing…' : 'Fetch Clips'}
        </button>
      </form>

      {error && <p className="mt-4 text-red-500">{error}</p>}

      {/* Display Clips */}
      {clips.length > 0 && (
        <div className="mt-6 space-y-4">
          <h2 className="text-xl font-semibold">Available Clips</h2>
          <ul className="space-y-2">
            {clips.map((c, i) => (
              <li key={i} className="border rounded p-2">
                <video
                  src={`${process.env.REACT_APP_API_BASE}${c.url}`}
                  controls
                  className="w-full rounded"
                />
                <p className="mt-1 text-sm text-gray-600">
                  {c.start}s – {c.end}s
                </p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
