import React, { useState } from 'react'
import axios from 'axios'

export default function App() {
  const [url, setUrl] = useState('')
  const [clips, setClips] = useState([])
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState('')

  const fetchClips = async e => {
    e.preventDefault()
    setLoading(true)
    setError('')
    setClips([])

    try {
    // 1) Get the latest videoId for this channelId (or URL if you choose to parse it later)
    const { data: vidData } = await axios.get(
      `${process.env.REACT_APP_API_BASE}/api/videos`,
      { params: { channelId: url } }
    );

    // 2) Now fetch clips for that video
    const { data: clipsData } = await axios.get(
      `${process.env.REACT_APP_API_BASE}/api/clips`,
      {
        params: {
          videoUrl: `https://youtu.be/${vidData.videoId}`
        }
      }
    );
    setClips(clipsData.clips);
  } catch (err) {
    setError(err.response?.data?.message || err.message);
  } finally {
    setLoading(false);
  }
};

  return (
    <div className="max-w-xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">AutoClipper-Web</h1>

      <form onSubmit={fetchClips} className="space-y-4">
        <div>
          <label className="block mb-1">
              YouTube Channel ID (e.g. UC_x5XG1OV2P6uZZ5FSM9Ttw)
          </label>
          <input
            type="text"
            className="w-full border rounded p-2"
            value={url}
            onChange={e => setUrl(e.target.value)}
            placeholder="Enter YouTube Channel ID or Video URL"
            required
          />
        </div>
        <button
          type="submit"
          disabled={loading}
          className="bg-blue-600 text-white px-4 py-2 rounded disabled:opacity-50"
        >
          {loading ? 'Analyzing…' : 'Fetch Clips'}
        </button>
      </form>

      {error && <p className="mt-4 text-red-500">{error}</p>}

      {clips.length > 0 && (
        <div className="mt-6">
          <h2 className="text-xl font-semibold mb-2">Available Clips</h2>
          <ul className="space-y-2">
            {clips.map((c, i) => (
              <li key={i} className="border rounded p-2">
                <video
                  src={c.url}
                  controls
                  className="w-full rounded"
                />
                <p className="mt-1 text-sm text-gray-600">
                  {c.start}s – {c.end}s
                </p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  )
}
