import React, { useState } from 'react';
import axios from 'axios';

export default function App() {
  const [url, setUrl] = useState('');
  const [clips, setClips] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Caption customization state
  const [captionStyle, setCaptionStyle] = useState({
    fontFamily: 'Arial',
    fontSize: 24,
    color: '#FFFFFF',
    outline: true,
    position: 'bottom',
  });

  // Background overlay selection
  const [background, setBackground] = useState('none');

  const fetchClips = async e => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setClips([]);

    try {
      let videoUrlParam = url.trim();

      // 1) Determine if input is a full URL or channel ID
      if (!/^https?:\/\//i.test(videoUrlParam)) {
        // treat as channel ID
        const { data: vidData } = await axios.get(
          `${process.env.REACT_APP_API_BASE}/api/videos`,
          { params: { channelId: videoUrlParam } }
        );
        videoUrlParam = `https://youtu.be/${vidData.videoId}`;
      }

      // 2) Fetch up to 3 clips
      const { data: clipsData } = await axios.get(
        `${process.env.REACT_APP_API_BASE}/api/clips`,
        { params: { videoUrl: videoUrlParam } }
      );
      setClips(clipsData.clips);
    } catch (err) {
      setError(err.response?.data?.message || err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">AutoClipper-Web</h1>

      <form onSubmit={fetchClips} className="space-y-4">
        <div>
          <label className="block mb-1">
            YouTube Channel ID (UCâ€¦) or Full Video URL
          </label>
          <input
            type="text"
            className="w-full border rounded p-2"
            value={url}
            onChange={e => setUrl(e.target.value)}
            placeholder="Enter channel ID or video URL"
            required
          />
        </div>
        <button
          type="submit"
          disabled={loading}
          className="bg-blue-600 text-white px-4 py-2 rounded disabled:opacity-50"
        >
          {loading ? 'Analyzingâ€¦' : 'Fetch Clips'}
        </button>
      </form>

      {error && <p className="mt-4 text-red-500">{error}</p>}

      {clips.length > 0 && (
        <div className="mt-6 space-y-6">
          <h2 className="text-xl font-semibold">Available Clips</h2>

          {/* Caption Styling Controls */}
          <div className="border rounded p-4">
            <h3 className="font-semibold mb-2">Caption Style</h3>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block">Font Family</label>
                <select
                  value={captionStyle.fontFamily}
                  onChange={e =>
                    setCaptionStyle({ ...captionStyle, fontFamily: e.target.value })
                  }
                  className="border rounded p-1 w-full"
                >
                  <option>Arial</option>
                  <option>Courier New</option>
                  <option>Georgia</option>
                  <option>Impact</option>
                  <option>Times New Roman</option>
                </select>
              </div>
              <div>
                <label className="block">Font Size</label>
                <input
                  type="number"
                  value={captionStyle.fontSize}
                  onChange={e =>
                    setCaptionStyle({ ...captionStyle, fontSize: Number(e.target.value) })
                  }
                  className="border rounded p-1 w-full"
                />
              </div>
              <div>
                <label className="block">Color</label>
                <input
                  type="color"
                  value={captionStyle.color}
                  onChange={e =>
                    setCaptionStyle({ ...captionStyle, color: e.target.value })
                  }
                  className="border rounded p-1"
                />
              </div>
              <div>
                <label className="inline-flex items-center mt-2">
                  <input
                    type="checkbox"
                    checked={captionStyle.outline}
                    onChange={e =>
                      setCaptionStyle({ ...captionStyle, outline: e.target.checked })
                    }
                    className="mr-2"
                  />
                  Outline
                </label>
              </div>
              <div className="col-span-2">
                <label className="block">Position</label>
                <select
                  value={captionStyle.position}
                  onChange={e =>
                    setCaptionStyle({ ...captionStyle, position: e.target.value })
                  }
                  className="border rounded p-1 w-full"
                >
                  <option value="bottom">Bottom</option>
                  <option value="top">Top</option>
                  <option value="center">Center</option>
                </select>
              </div>
            </div>
          </div>

          {/* Background Overlay Selection */}
          <div className="border rounded p-4">
            <h3 className="font-semibold mb-2">Background Overlay</h3>
            <select
              value={background}
              onChange={e => setBackground(e.target.value)}
              className="border rounded p-2 w-full"
            >
              <option value="none">None</option>
              <option value="minecraft">Minecraft Parkour</option>
              <option value="gta5">GTA5 Gameplay</option>
              <option value="rocketleague">Rocket League</option>
            </select>
            <p className="mt-2 text-sm text-gray-500">
              (Backgrounds are fetched & trimmed automatically.)
            </p>
          </div>

          {/* Clip Previews */}
          <ul className="space-y-4">
            {clips.map((c, i) => (
              <li key={i} className="border rounded p-2">
                <video
                  src={`${process.env.REACT_APP_API_BASE}${c.url}`}
                  controls
                  className="w-full rounded"
                />
                <p className="mt-1 text-sm text-gray-600">
                  {c.start}s â€“ {c.end}s
                </p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
